package com.oyetalk.example1.domain.request;import com.cyber.domain.entity.PagingRequest;import com.oyetalk.example1.domain.entity.Application;import org.springframework.beans.BeanUtils;public class ApplicationRequest extends PagingRequest {	private String applicationCode;	private String applicationName;	public Application toEvent() {		Application application = new Application();		BeanUtils.copyProperties(this, application);		return application;	}	public String getApplicationCode() {	    return this.applicationCode;	}	public void setApplicationCode(String applicationCode) {	    this.applicationCode=applicationCode;	}	public String getApplicationName() {	    return this.applicationName;	}	public void setApplicationName(String applicationName) {	    this.applicationName=applicationName;	}}