package com.oyetalk.example1.domain.request;import com.cyber.domain.entity.OperateEntity;import com.oyetalk.example1.domain.entity.Application;import org.springframework.beans.BeanUtils;import java.util.Date;import java.util.UUID;public class CreateApplicationRequest extends OperateEntity {	private String applicationCode;	private String applicationName;	public Application toEvent(String userCode) {		Application application = new Application();		BeanUtils.copyProperties(this, application);		String id = UUID.randomUUID().toString();		application.setId(id);		application.setCreatorId(userCode);		application.setCreateTime(new Date());		application.setUpdatorId(userCode);		application.setUpdateTime(new Date());		return application;	}	public String getApplicationCode() {	    return this.applicationCode;	}	public void setApplicationCode(String applicationCode) {	    this.applicationCode=applicationCode;	}	public String getApplicationName() {	    return this.applicationName;	}	public void setApplicationName(String applicationName) {	    this.applicationName=applicationName;	}}