package com.oyetalk.example1.domain.request;import com.cyber.domain.entity.IdRequest;import com.oyetalk.example1.domain.entity.Application;import org.springframework.beans.BeanUtils;import java.util.Date;public class UpdateApplicationRequest extends IdRequest {	private String applicationCode;	private String applicationName;	public Application toEvent(String userCode) {		Application application = new Application();		BeanUtils.copyProperties(this, application);		application.setUpdatorId(userCode);		application.setUpdateTime(new Date());		return application;	}	public String getApplicationCode() {	    return this.applicationCode;	}	public void setApplicationCode(String applicationCode) {	    this.applicationCode=applicationCode;	}	public String getApplicationName() {	    return this.applicationName;	}	public void setApplicationName(String applicationName) {	    this.applicationName=applicationName;	}}