package com.oyetalk.iot.domain.request;import com.cyber.domain.entity.OperateEntity;import com.oyetalk.iot.domain.entity.Iot;import org.springframework.beans.BeanUtils;import java.util.Date;import java.util.UUID;public class CreateIotRequest extends OperateEntity {	private String deviceType;	private String deviceTypeName;	private String deviceName;	private String deviceCover;	private String deviceStatus;	private String deviceStatusName;	private String devicePosition;	private String devicePositionName;	public Iot toEvent(String userCode) {		Iot iot = new Iot();		BeanUtils.copyProperties(this, iot);		String id = UUID.randomUUID().toString();		iot.setId(id);		iot.setDeviceId(id);		iot.setCreatorId(userCode);		iot.setCreateTime(new Date());		iot.setUpdatorId(userCode);		iot.setUpdateTime(new Date());		return iot;	}	public String getDeviceType() {	    return this.deviceType;	}	public void setDeviceType(String deviceType) {	    this.deviceType=deviceType;	}	public String getDeviceTypeName() {	    return this.deviceTypeName;	}	public void setDeviceTypeName(String deviceTypeName) {	    this.deviceTypeName=deviceTypeName;	}	public String getDeviceName() {	    return this.deviceName;	}	public void setDeviceName(String deviceName) {	    this.deviceName=deviceName;	}	public String getDeviceCover() {	    return this.deviceCover;	}	public void setDeviceCover(String deviceCover) {	    this.deviceCover=deviceCover;	}	public String getDeviceStatus() {	    return this.deviceStatus;	}	public void setDeviceStatus(String deviceStatus) {	    this.deviceStatus=deviceStatus;	}	public String getDeviceStatusName() {	    return this.deviceStatusName;	}	public void setDeviceStatusName(String deviceStatusName) {	    this.deviceStatusName=deviceStatusName;	}	public String getDevicePosition() {	    return this.devicePosition;	}	public void setDevicePosition(String devicePosition) {	    this.devicePosition=devicePosition;	}	public String getDevicePositionName() {	    return this.devicePositionName;	}	public void setDevicePositionName(String devicePositionName) {	    this.devicePositionName=devicePositionName;	}}