package com.oyetalk.iot.domain.request;import com.cyber.domain.entity.PagingRequest;import com.oyetalk.iot.domain.entity.Iot;import org.springframework.beans.BeanUtils;public class IotRequest extends PagingRequest {	private String deviceType;	private String deviceStatus;	private String devicePosition;	public Iot toEvent() {		Iot iot = new Iot();		BeanUtils.copyProperties(this, iot);		return iot;	}	public String getDeviceType() {		return deviceType;	}	public void setDeviceType(String deviceType) {		this.deviceType = deviceType;	}	public String getDeviceStatus() {		return deviceStatus;	}	public void setDeviceStatus(String deviceStatus) {		this.deviceStatus = deviceStatus;	}	public String getDevicePosition() {		return devicePosition;	}	public void setDevicePosition(String devicePosition) {		this.devicePosition = devicePosition;	}}